import React, { useState } from 'react'
import { useLocation } from 'react-router-dom';
import './style/Detail.css'
import genre from './assets/genre.json'

export default function Detail() {
    const location = useLocation();
    const moviedetails = location.state.movie;
    const IMG_URL = `https://image.tmdb.org/t/p/original/`;
    let year = "";
    let i = 0;
    while (i < 4) {
        year += moviedetails.release_date[i];
        i += 1;
    }
    let lang = moviedetails.original_language;
    if (lang == "en") {
        lang = "English";
    } else if (lang == "es") {
        lang = "Spanish";
    } else if (lang == "hi"){
        lang = "Hindi";
    } else if (lang == "ta"){
        lang = "Tamil";
    } else if (lang == "kn"){
        lang = "Kannada";
    } else if (lang == "fr"){
        lang = "French";
    } else if (lang == "ja"){
        lang = "French";
    }
    const movieGenre = [];
    genre.forEach((g) => {
        for (let i = 0; i < moviedetails.genre_ids.length; i++) {
            if (g.id == moviedetails.genre_ids[i]) {
                movieGenre.push(g);
            }
        }
    })
    console.log(movieGenre);
    return (
        <div className='detail-cont'>
            <div className='intro' style={{ backgroundImage: `url(${IMG_URL}/${moviedetails.backdrop_path})` }}>
                <div className='info-cont'>
                    <h1>{moviedetails.title}</h1>
                    <div className='year-lang'>
                        <div>•</div>
                        <div>{year}</div>
                        <div>•</div>
                        <div>{lang}</div>
                    </div>

                </div>
            </div>
            <div className='down-details'>
                <div className='overview'>
                    <h3>Overview</h3>
                    {moviedetails.overview}
                </div>
                <div className='genre'>
                <h3>Movie Genre</h3>
                    <div className='genre-list'>
                    {movieGenre.map((g, index) => (
                        <div key={g.id}>{g.name}</div>
                    ))
                    }
                    </div>
                </div>
            </div>
        </div>
    )
}
