import React, { useEffect, useState } from 'react';
import simple from './assets/simple.json';
import Discover from './components/Discover';
import SearchIt from './components/SearchIt';

export default function Home() {

    // FETCH POPULAR MOVIES
    const [movielist, setMovielist] = useState([]);
    const fetchmovie = async () => {
        const API_URL_POP = `https://api.themoviedb.org/3/movie/popular?api_key=dc6c9756c455e1b3b3f8d3b91062b234&language=en-US&page=1`
        const popular_response = await fetch(API_URL_POP);
        const popular_response_json = await popular_response.json();
        if (popular_response_json.results){
            setMovielist(popular_response_json.results);
        }
    }
    useEffect(() => {
        fetchmovie()
    }, [])

    // FETCH SEARCH RESULTS
    const [searchmovie, setSearchmovie] = useState('');
    const handleSearchmovie = (e) => {
        setSearchmovie(e.target.value);
    }
    const fetchResults = async (searchmovie) => {
        const API_URL_SEARCH = `https://api.themoviedb.org/3/search/movie?api_key=dc6c9756c455e1b3b3f8d3b91062b234&language=en-US&query=${searchmovie}`
        const response = await fetch(API_URL_SEARCH);
        const responseJson = await response.json();
        if (responseJson.results){
            setSearchResults(responseJson.results);
        }
    }
    useEffect(() => {
        fetchResults(searchmovie);
    },[searchmovie])
    const [searchresults, setSearchResults] = useState([]);


    return (
        <div className='home-cont'>
            <div className='nav-seg'>
                <h2>Title</h2>
                <input type='text' value={searchmovie} onChange={handleSearchmovie} />
            </div>
            <div>
                <SearchIt />
            </div>
            <div>
                <Discover movieslist={movielist} />
            </div>
        </div>
    )
}
