import React, { useState } from 'react'
import { useLocation } from 'react-router-dom';
import './style/Detail.css'
import genre from './assets/genre.json'

export default function Detail() {
    const location = useLocation();
    const moviedetails = location.state.movie;
    const IMG_URL = `https://image.tmdb.org/t/p/original/`;
    let year = "";
    let i = 0;
    while (i < 4) {
        year += moviedetails.release_date[i];
        i += 1;
    }
    let lang = moviedetails.original_language;
    if (lang == "en") {
        lang = "English";
    }
    console.log(genre);
    const [moviegenre,setMoviegenre] = useState([]);
    for (let i = 0;i<moviedetails.genre_ids.length;i++){
        for (let j = 0;j<19;j++){
            if (genre[j].id == moviedetails[j]){
                setMoviegenre(genre[j]);
            }
        }
    }
    con
    return (
        <div className='detail-cont'>
            <div className='intro' style={{ backgroundImage: `url(${IMG_URL}/${moviedetails.backdrop_path})` }}>
                <div className='info-cont'>
                    <h1>{moviedetails.title}</h1>
                    <div className='year-lang'>
                        <div>{year}</div>
                        <div>{lang}</div>
                    </div>
                    <div className='genre-list'>
                        <div></div>
                    </div>
                </div>
            </div>
            <div>

            </div>
        </div>
    )
}
