import React from "react";
import "../style/Card.css";
import Details from "./Details";
import { useNavigate, Routes, Route, Link, Router, useHistory } from "react-router-dom";
import { Home } from "../Home";

function Card({ movie }) {
  const history = useHistory();
  const handleClick = (movie) => {
    console.log("Clicked");
    console.log(movie.title);
  };
  return (
    <>
      <div>
        <div className="img-card" onClick={() => history.push('/detail')}>
          <div className="card-title">{movie.title}</div>
          <img src={require("../images/one.jpg")} width={280} />
        </div>
      </div>
    </>
  );
}

export default Card;
