import React, { useEffect, useState } from 'react';
import simple from './assets/simple.json';
import Discover from './components/Discover';

export default function Home() {

    const [movielist, setMovielist] = useState([]);
    const fetchmovie = () => {
        setMovielist(simple)
    }
    useEffect(() => {
        fetchmovie()
    },[])

    const [searchmovie, setSearchmovie] = useState('');
    const handleSearchmovie = (e) => {
        setSearchmovie(e.target.value);
    }
    let results = [];
    for (var i = 0;i<movielist.length;i++){
        for (var j in movielist[i]){
            console.log(movielist[i].title);
            if (movielist[i].title.lower == searchmovie){
                results.push(movielist[i]);
            }
        }
    }
    console.log(results);
    return (
        <div className='home-cont'>
            <div className='nav-seg'>
                <h2>Title</h2>
                <input type='text' value={searchmovie} onChange={handleSearchmovie}/>
            </div>
            <div>
            </div>
            <div>
                <Discover movieslist={movielist}/>
            </div>
            <div className='foot-seg'></div>
        </div>
    )
}
