import React from "react";
import "../style/Card.css";
import Details from "./Details";

function Card({ movie }) {
  const handleClick = (movie) => {
    console.log("Clicked");
    <Details movie={movie} />
  }
  return (
    <>
      <div>
        <div className="img-card" onClick={() => handleClick({movie})}>
          <div className="card-title">{movie.title}</div>
          <img src={require("../images/one.jpg")} width={280} />
        </div>
      </div>
    </>
  );
}

export default Card;
