    const VIDEO_URL = `https://api.themoviedb.org/3/movie/{movie_id}/videos?api_key=dc6c9756c455e1b3b3f8d3b91062b234&language=en-US`
    return (
        <div>
            <h2>{moviedetails.title}</h2>
            <div>
            <img width='100%' src={`${IMG_URL}/${moviedetails.backdrop_path}`} />

