import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Splash() {
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);
  }, []);
  return (
    <div className="App">
      {loading ? (
        <div className="main">
          <header>
            <h1>Cinemize</h1>
            <h3>The popcorn time</h3>
          </header>
          <footer>
            <ul>
              <li>
                <a href="">Privacy Policy</a>
              </li>
              <li>
                <a href="">Terms & Conditions</a>
              </li>
              <li>
                <a href="">Help</a>
              </li>
            </ul>
          </footer>
        </div>
      ) : (
        <Home />
      )}
    </div>
  );
}
