import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom';
import './style/Detail.css'

export default function Detail() {
    const location = useLocation();
    const movieID = location.state.movie.id;
    const moviedetails = location.state.movie;
    const IMG_URL = `https://image.tmdb.org/t/p/original/`;
    const DETAIL_URL = `https://api.themoviedb.org/3/movie/${movieID}?api_key=dc6c9756c455e1b3b3f8d3b91062b234&append_to_response=videos`;
    const selectedMovie = async () => {
        const {data} = await fetch(DETAIL_URL);
        return data;
    }
    const [selMov,setSelmov] = useState([]);
    const selectedMovieList = () => {
        const movie_det = selectedMovie();

    };
    return (
        <div className='detail-cont'>
            <div className='intro' style={{ backgroundImage: `url(${IMG_URL}/${moviedetails.backdrop_path})` }}>
                <div className='info-cont'>
                    <h1>{moviedetails.title}</h1>
                </div>
            </div>
            <div className='down-details'>
                <div className='overview'>
                    <h3>Overview</h3>
                    {moviedetails.overview}
                </div>
            </div>
        </div>
    )
}
