import React, { useEffect, useState } from 'react';
import simple from './assets/simple.json';
import Discover from './components/Discover';

export default function Home() {

    const [movielist, setMovielist] = useState([]);
    const fetchmovie = () => {
        setMovielist(simple)
    }
    useEffect(() => {
        fetchmovie()
    },[])

    const [searchmovie, setSearchmovie] = useState('');
    const handleSearchmovie = (e) => {
        setSearchmovie(e.target.value);
        console.log(searchmovie);
    }

    const [searchresults, setSearchresults] = useState([]);
    const handleSearchresult = (searchmovie) => {
        var j;
        for (var i = 0;i<movielist.length;i++){
            for ( j in movielist[i]){
                if (movielist[i][j].indexOf(searchmovie) != -1){
                    setSearchresults.push(movielist[i]);
                }
            }
        }
    }
    console.log(searchresults);
    return (
        <div className='home-cont'>
            <div className='nav-seg'>
                <h2>Title</h2>
                <input type='text' value={searchmovie} onChange={handleSearchmovie}/>
            </div>
            <div>
                {handleSearchresult('avatar')}
            </div>
            <div>
                <Discover movieslist={movielist}/>
            </div>
            <div className='foot-seg'></div>
        </div>
    )
}
