    //         setSearchlist(item.title.toLowerCase().includes(searchmovie))
    //     })
    // }
    // useEffect(() => {
    //     searchResult()
    // })

    console.log(searchlist);

    return (
        <div className='home-cont'>
            <div className='nav-seg'>
                <h2>Title</h2>
                <input type='text' value={searchmovie} onChange={handleSearchmovie} />
            </div>
            <div>
                <Discover movieslist={searchlist}/>
            </div>
            <div>
                <Discover movieslist={movielist} />
            </div>
        </div>
    )
}
